manual/meta/package_python/docs.schema_str:
$schema: https://json-schema.org/draft/2020-12/schema
title: meta_package_docs
description: Package documentation settings
type: object
default: { }
additionalProperties: false
properties:
  docs:
    type: object
    default: { }
    additionalProperties: false
    properties:
      main_init:
        type: string
        default: |
          ${{name}}

          ${{tagline}}

          ${{description}}

          Copyright ${{copyright.notice}}

          ${{license.notice}}
    required: [ main_init ]
required: [ docs ]

manual/meta/package_python/metadata.default_str:
manifest:
- graft ${{ path.dir.source }}/${{ package.name }}
- include CODE_OF_CONDUCT.md
- global-exclude *.py[cod] __pycache__ *.so
typed: true
trove_classifiers: []
urls:
  Homepage: ${{ url.website.base }}
  Download: ${{ url.github.releases.home }}
  News: ${{ url.website.news }}
  Documentation: ${{ url.website.base }}
  Issue-Tracker: ${{ url.github.issues.home }}
  Sponsor: ${{ url.website.sponsor }}
  Source: ${{ url.github.home }}

entry.intro:
- Commit Type: {commit_type}
- Commit Title: {commit_title}
- Parent Commit: [{parent_commit_hash}]({parent_commit_url})

attributes.value:
import ${{package.name]}

def unit_test():
    # write a unit-test here to reproduce the bug
    ...
    assert ...
